mapper.select:
SELECT 

FROM account a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account = #{account} 


		AND a.password = #{password}

SELECT 

FROM account a

WHERE a.id = #{id}

SELECT 

FROM account a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account LIKE 
			'%'||#{account}||'%'
			'%'+#{account}+'%'
			concat('%',#{account},'%')


		AND a.guan = #{guan}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.durmnumber = #{durmnumber}


		AND a.hometown = #{hometown}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM account a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE account SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
